mapper.select:
SELECT maintenanceid,priority,entnyname,grade,perentid,`index`,codeno,unit,workload,changeworkload ,begindate,planfinishdate,accumulatedcompletion,accumulationcompletionrate ,DATE_FORMAT(begindate,'%Y-%m-%d') as begindatestr ,DATE_FORMAT(planfinishdate,'%Y-%m-%d') as planfinishdatestr FROM t_f_maintenance where 1 = 1
and maintenanceid = #{maintenanceid}
order by codeno

SELECT
maintenanceid,priority,entnyname,grade,perentid,`index`,codeno,unit
FROM
t_f_maintenance where 1 = 1

 and grade = #{grade}



 and perentid = #{perentid} 


 and maintenanceid = #{maintenanceid} 

order by codeno

mapper.update:
UPDATE t_f_maintenance
SET changeworkload =#{changeworkload}
WHERE maintenanceid = #{maintenanceid}
