mapper.update:
update t_sys_menu 
set menuname = #{menuname},url = #{url},ismenu = #{ismenu},pid = #{pid},
priority = #{priority}
where id=#{id}

mapper.select:
select a.id,a.menuname,a.url,a.priority,a.ismenu,a.pid,b.menuname pmenuname from t_sys_menu a left join t_sys_menu b on a.pid=b.id where a.id in(select menuid from t_sys_role_menu where roleid in (select roleid from t_sys_user_role where 1=1
and userid = #{userid}
)) and a.isdisabled=0 order by a.priority

mapper.update:
update t_sys_menu set isdisabled = 1 where 1=1
and id in
#{idList}
