mapper.select:
select * from t_s_suboffice
where isonlysubo = 1 and isdisabled = 0

	and subofficeid = #{subofficeid}

select *, case when isonlysubo = '1' then '是' else '否' end isonlysubostr from t_s_suboffice where 1=1 and isdisabled = 0
and subofficeid = #{subofficeid}
and subofficename like CONCAT('%',#{subofficename},'%')

select *, case when isonlysubo = '1' then '是' else '否' end isonlysubostr from t_s_suboffice where 1=1 and isdisabled = 0
and subofficeid = #{subofficeid}
and subofficename like CONCAT('%',#{subofficename},'%')

mapper.update:
update t_s_suboffice 
set subofficename = #{subofficename},
isonlysubo = #{isonlysubo}
where subofficeid=#{subofficeid}

update t_s_suboffice set isdisabled = 1 where 1=1
and subofficeid in
#{idList}
