mapper.select:
select 
	t.*,st.subofficename
from t_sys_user t
left join t_s_suboffice st on t.subofficeid = st.subofficeid
where 1=1

	and t.username = #{username} 


	and t.password = #{password} 


	and t.id = #{userid}

mapper.update:
update t_sys_user 
set subofficeid = #{subofficeid},username = #{username},realname = #{realname},password = #{password},
sex = #{sex},email = #{email}
where id=#{id}

update t_sys_user set isdisabled = 1 where 1=1
and id in
#{idList}
